let current

chrome.runtime.onMessage.addListener(function (request, sender, sendResponse){
    if ('initialStart' in request) {
        sendResponse(true)
    }

    if ('start' in request) {
        const priceElems = document.getElementsByClassName('js-price');

        if (!priceElems.length) {
            sendResponse('ERROR: no closing bet')
        } else {
            sendResponse('started')
            if (current.targetClassName === 'js-blue-cell') {
                console.log(document.getElementsByClassName('biab_lay-0')[current.elem].children[0].children[0].children[0].children[0].textContent)
            }
            if (current.targetClassName === 'js-green-cell') {
                console.log(document.getElementsByClassName('biab_back-0')[current.elem].children[0].children[0].children[0].children[0].textContent)
            }

            const interval = setInterval(() => {
                const marginOdds = priceElems[0].value
                const watchedOdds = document.getElementsByClassName(current.targetClassName)[current.elem].children[0].children[0].children[0].children[0]
                const marginLayOdds = document.getElementsByClassName('biab_lay-2')[current.elem].children[0].children[0].children[0].children[0]
                const marginBackOdds = document.getElementsByClassName('biab_back-2')[current.elem].children[0].children[0].children[0].children[0]
                const profitLabel = document.getElementsByClassName('js-selection-pl-wrap')[current.elem === 0 ? 1 : 0]
                console.log('inside interval')

                if (parseFloat(profitLabel.textContent) === 0) {
                    if (current.targetClassName === 'js-blue-cell' && parseFloat(marginOdds) < parseFloat(marginBackOdds.textContent)) {
                        console.log('adjusting bet up')
                        adjustBet('up');
                    }
                    if (current.targetClassName === 'js-green-cell' && parseFloat(marginOdds) > parseFloat(marginLayOdds.textContent)) {
                        console.log('adjusting bet down')
                        adjustBet('down');
                    }
                }
                if (current.targetClassName === 'js-blue-cell' && parseFloat(marginOdds) >= parseFloat(watchedOdds.textContent)
                    || current.targetClassName === 'js-green-cell' && parseFloat(marginOdds) <= parseFloat(watchedOdds.textContent)) {
                    clearInterval(interval)
                    makeSound()
                    placeBet()
                }
            }, 1000)
        }
    }

    if ('next' in request) {
        const elem = request.next.elem
        const type = request.next.type

        const targetClassName = type ? 'js-blue-cell' : 'js-green-cell'

        try {
            const target = document.getElementsByClassName(targetClassName)[elem]
            target.style.border = '2px solid black'

            if (current) {
                const prevTarget = document.getElementsByClassName(current.targetClassName)[current.elem]
                prevTarget.style.border = ''
            }

            current = {elem, targetClassName}

            sendResponse('next: received')
        } catch (err) {
            sendResponse(elem - 1)
        }
    }

    function getMouseDownEvent() {
        return new MouseEvent("mousedown", {
            view: window,
            bubbles: true,
            cancelable: true,
            clientX: 0,
            clientY: 0,
        });
    }

    function adjustBet(direction) {
        const buttons = document.getElementsByClassName('biab_btn-custom-number js-price-btn');
        if (direction === 'up') {
            buttons[0].click();
        }
        if (direction === 'down') {
            buttons[1].click();
        }
    }

    function placeBet() {
        const button = document.getElementById('biab_placeBetsBtn')
        const confirmButton = document.getElementById('biab_confirmBtn')

        const evt = getMouseDownEvent();

        if (button) {
            button.dispatchEvent(evt)
            setTimeout(() => {
                placeBet()
            }, 5000)
        }

        if (confirmButton) {
            confirmButton.dispatchEvent(evt)
            setTimeout(() => {
                placeBet()
            }, 5000)
        }
    }

    function makeSound() {
        setInterval(() => {
            var snd = new Audio("data:audio/wav;base64,")
            snd.play();
        }, 1000)
    }
});
